import React from "react";
import Autocomplete from "react-google-autocomplete";

const SearchBar = ({ onPlaceSelected }) => {
  return (
    <div className="search-bar">
      <Autocomplete
        apiKey="AIzaSyDpm7myIBptTbd4kXwpSeYeCeC37AAupy0" // 替換為您的 Google Maps API 金鑰
        onPlaceSelected={(place) => {
          console.log("Selected place:", place); // 調試輸出選中地點

          // 檢查 place 是否有效且包含地理位置
          if (place.geometry && place.geometry.location) {
            const location = {
              lat: place.geometry.location.lat(),
              lng: place.geometry.location.lng(),
            };
            console.log("Extracted location:", location); // 調試輸出經緯度
            onPlaceSelected(location); // 將地點回調傳遞給父組件
          } else {
            alert("請輸入或選擇有效的地點！");
          }
        }}
        options={{
          types: ["geocode"], // 限制自動完成為地理位置類型
        }}
        placeholder="搜尋地點..."
        style={{
          width: "300px",
          padding: "10px",
          fontSize: "16px",
        }}
      />
    </div>
  );
};

export default SearchBar;
