import React, { useState } from "react";

const SearchBar = ({ onSearch, onLocationSearch }) => {
  const [input, setInput] = useState("");

  const handleSearch = () => {
    onSearch(input); // 搜尋拉麵店
  };

  const handleLocationSearch = () => {
    onLocationSearch(input); // 搜尋地點，移動地圖
  };

  return (
    <div className="search-bar">
      <input
        type="text"
        placeholder="輸入地點名稱或現在所在地"
        value={input}
        onChange={(e) => setInput(e.target.value)}
      />
      <button onClick={handleSearch}>搜尋拉麵店</button>
      <button onClick={handleLocationSearch}>跳至所在地</button>
    </div>
  );
};

export default SearchBar;
