import React, { useState, useRef } from "react";
import SearchBar from "./components/SearchBar";
import RamenList from "./components/RamenList";
import Map from "./components/Map";
import Weather from "./components/Weather";

const App = () => {
  const [ramenShops, setRamenShops] = useState([]);
  const [locations, setLocations] = useState([]);
  const [center, setCenter] = useState({ lat: 25.033964, lng: 121.564468 }); // 預設為台北101
  const mapRef = useRef(null); // 用於控制地圖

  // 搜尋拉麵店
  const fetchRamenShops = async (query) => {
    const response = await fetch(
      `https://maps.googleapis.com/maps/api/place/textsearch/json?query=ramen+near+${query}&key=YOUR_GOOGLE_MAPS_API_KEY`
    );
    const data = await response.json();
    const shops = data.results.map((shop) => ({
      name: shop.name,
      address: shop.formatted_address,
      location: {
        lat: shop.geometry.location.lat,
        lng: shop.geometry.location.lng,
      },
    }));
    setRamenShops(shops);
    setLocations(shops.map((shop) => shop.location));
    if (shops.length > 0) {
      setCenter(shops[0].location); // 將地圖中心設置為第一個拉麵店的位置
    }
  };

  // 搜尋地點，並移動地圖到該地點
  const fetchLocation = async (locationName) => {
    const response = await fetch(
      `https://maps.googleapis.com/maps/api/geocode/json?address=${encodeURIComponent(
        locationName
      )}&key=YOUR_GOOGLE_MAPS_API_KEY`
    );
    const data = await response.json();
    if (data.results.length > 0) {
      const newCenter = {
        lat: data.results[0].geometry.location.lat,
        lng: data.results[0].geometry.location.lng,
      };
      setCenter(newCenter); // 更新中心點
      if (mapRef.current) {
        mapRef.current.panTo(newCenter); // 平移地圖到新位置
      }
    } else {
      alert("無法找到該地點，請再試一次！");
    }
  };

  return (
    <div className="app">
      <h1>拉麵店尋找系統</h1>
      <SearchBar onSearch={fetchRamenShops} onLocationSearch={fetchLocation} />
      <RamenList ramenShops={ramenShops} />
      <Map locations={locations} center={center} mapRef={mapRef} />
      <Weather lat={center.lat} lon={center.lng} />
    </div>
  );
};

export default App;
