import React, { useRef } from "react";
import Autocomplete from "react-google-autocomplete";

const SearchBar = ({ onPlaceSelected }) => {
  const autocompleteRef = useRef(null);

  const handleSearch = () => {
    if (!autocompleteRef.current) {
      alert("自動完成搜尋框尚未加載，請稍後再試！");
      return;
    }

    const place = autocompleteRef.current.getPlace();
    console.log("Selected place:", place); // 調試輸出地點資訊

    if (place && place.geometry && place.geometry.location) {
      const location = {
        lat: place.geometry.location.lat(),
        lng: place.geometry.location.lng(),
      };
      console.log("Extracted location:", location); // 調試輸出經緯度
      onPlaceSelected(location);
    } else {
      alert("請輸入或選擇有效的地點！");
    }
  };

  return (
    <div className="search-bar">
      <Autocomplete
  apiKey="AIzaSyDpm7myIBptTbd4kXwpSeYeCeC37AAupy0" // 替換為您的 Google API 金鑰
  libraries={["places"]} // 確保載入 Places API
  onLoad={(autocomplete) => {
    console.log("Autocomplete loaded:", autocomplete); // 驗證加載成功
    autocompleteRef.current = autocomplete;
  }}
  onPlaceSelected={(place) => {
    console.log("Place selected:", place);
    if (place.geometry && place.geometry.location) {
      const location = {
        lat: place.geometry.location.lat(),
        lng: place.geometry.location.lng(),
      };
      onPlaceSelected(location);
    }
  }}
  options={{
    types: ["geocode"], // 限制為地理位置
  }}
  placeholder="搜尋地點..."
  style={{
    width: "300px",
    padding: "10px",
    fontSize: "16px",
  }}
/>
      <button
        onClick={handleSearch}
        style={{
          padding: "10px 15px",
          marginLeft: "10px",
          fontSize: "16px",
          cursor: "pointer",
        }}
      >
        搜尋
      </button>
    </div>
  );
};

export default SearchBar;
