import React, { useState, useRef } from "react";
import SearchBar from "./components/SearchBar";
import Map from "./components/Map";
import Weather from "./components/Weather";

const ramenList = [
  "一風堂拉麵",
  "屯京拉麵",
  "北海道拉麵",
  "無敵家拉麵",
  "山頭火拉麵",
  "麵屋武藏",
  "麵屋一燈",
];

const App = () => {
  const [center, setCenter] = useState({ lat: 25.033964, lng: 121.564468 }); // 預設台北101
  const mapRef = useRef(null);

  const handlePlaceSelected = (location) => {
    setCenter(location); // 更新地圖中心
    if (mapRef.current) {
      mapRef.current.panTo(location); // 平移地圖到新位置
    }
  };

  return (
    <div className="app">
      <h1>拉麵搜尋系統</h1>
      <div className="ramen-list">
        <h2>台北市有名的拉麵店：</h2>
        <ul>
          {ramenList.map((ramen, index) => (
            <li key={index}>{ramen}</li>
          ))}
        </ul>
      </div>
      <SearchBar onPlaceSelected={handlePlaceSelected} />
      <Map center={center} mapRef={mapRef} />
      <Weather lat={center.lat} lon={center.lng} />
    </div>
  );
};

export default App;
